import React from 'react';
import "./Card.css";
import { MoreHorizontal } from 'react-feather';

const Card = ( props ) => {
    const { ticket, groupBy, users } = props;

  const getUserById = (userId) => {
    return users.find((user) => user.id === userId);
  };

  const getInitials = (name) => {
    const nameParts = name.split(' ');
    const firstName = nameParts[0];
    const lastName = nameParts.length > 1 ? nameParts[1] : '';
    const initials = `${firstName.charAt(0)}${lastName ? lastName.charAt(0) : ''}`;
    return initials.toUpperCase();
  };

  const getRandomColor = () => {
    const colors = [
      '#F44336',
      '#E91E63',
      '#9C27B0',
      '#673AB7',
      '#3F51B5',
      '#2196F3',
      '#03A9F4',
      '#00BCD4',
      '#009688',
      '#4CAF50',
      '#8BC34A',
      '#CDDC39',
      '#FFC107',
      '#FF9800',
      '#FF5722',
    ];
    return colors[Math.floor(Math.random() * colors.length)];
  };

  const user = getUserById(ticket.userId);
  const initials = user ? getInitials(user.name) : '';

  return (
    <div className='card'>
      <div className='card-top'>
        <p className='card-title'>{props.ticket.id}</p>
        {props.groupBy !== 'user' && (
            <div
            className="user-pic"
            style={{
              backgroundColor: getRandomColor(),
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              borderRadius: '50%',
              width: '48px',
              height: '48px',
              color: 'white',
              fontSize: '18px',
            }}
          >
            {initials}
          </div>
        )}

      </div>
      <div className='card-middle'>
        {props.ticket.status === 'Backlog' && props.groupBy !== 'status' && (
          <img src="https://cdn-icons-png.flaticon.com/128/10905/10905332.png" className='logo-pic' alt="Backlog" />
        )}
        {props.ticket.status === 'Todo' && props.groupBy !== 'status' && (
          <img src="https://cdn-icons-png.flaticon.com/128/446/446163.png" className='logo-pic' alt="Todo" />
        )}
        {props.ticket.status === 'In progress' && props.groupBy !== 'status' && (
          <img src="https://cdn-icons-png.flaticon.com/128/5460/5460873.png" className='logo-pic' alt="In progress" />
        )}
        {props.ticket.status === 'Done' && props.groupBy !== 'status' && (
          <img src="https://cdn-icons-png.flaticon.com/128/5461/5461184.png" className='logo-pic' alt="Done" />
        )}
        {props.ticket.status === 'Cancelled' && props.groupBy !== 'status' && (
          <img src="https://cdn-icons-png.flaticon.com/128/1008/1008968.png" className='logo-pic' alt="Cancelled" />
        )}
        <p className='card-desc bold'>
          {props.ticket.title}
        </p>
      </div>
      <div className='card-bottom'>
        {props.groupBy !== 'priority' && (
          <div className='component-1 pointer'>
            <MoreHorizontal className='icon' />
          </div>
        )}
        <div className='component-2 pointer'>
          <img
            src="https://cdn-icons-png.flaticon.com/128/5720/5720434.png"
            className='icon'
            alt='logo'
          />
          <p>{props.ticket.tag[0]}</p>
        </div>
      </div>
    </div>
  );
};

export default Card
