import React from 'react';
import "./Card.css";
import { MoreHorizontal } from 'react-feather';

const Card = ( props ) => {
    const userLabels = users.reduce((labels, user) => {
        const nameParts = user.name.split(' '); // Split the name into parts by space
        const firstName = nameParts[0]; // Get the first part as the first_name
        const lastName = nameParts.length > 1 ? nameParts[1] : ''; // Get the second part as the last_name if it exists
    
        const firstLetterFirstName = firstName.charAt(0).toUpperCase(); // Get the first letter of first_name and convert to uppercase
        const firstLetterLastName = lastName.charAt(0).toUpperCase(); // Get the first letter of last_name and convert to uppercase
    
        const randomColor = getRandomColor(); // Generate a random color for the circular background
    
        labels[user.id] = (
          <div className="user-label">
            <div
              className="user-pic"
              style={{
                backgroundColor: randomColor,
                display: 'flex', // Use flexbox to center content
                alignItems: 'center', // Vertically center content
                justifyContent: 'center', // Horizontally center content
                borderRadius: '50%', // Make it circular
                width: '48px', // Set a fixed width and height for the circle
                height: '48px',
                color: 'white', // Set the text color to white
                fontSize: '18px', // Adjust the font size as needed
              }}
            >
              {firstLetterFirstName}
              {lastName && ` ${firstLetterLastName}`} {/* Display the last_name if it exists */}
            </div>
            {user.name}
          </div>
        );
        return labels;
      }, {});
  return (
    <div className='card'>
        <div className='card-top'>
            <p className='card-title'>{props.ticket.id}</p>
            {props.groupBy !== 'user' && (
                <img
                src="https://cdn-icons-png.flaticon.com/128/1144/1144709.png"
                className='profile-pic'
                alt='profile-pic'
                />
            )}
        </div>
        <div className='card-middle'>
            {props.ticket.status==='Backlog' && props.groupBy !== 'status' && (
                <img src="https://cdn-icons-png.flaticon.com/128/10905/10905332.png" className='logo-pic' alt="Backlog" />
            )}
            {props.ticket.status==='Todo' && props.groupBy !== 'status' && (
                <img src="https://cdn-icons-png.flaticon.com/128/446/446163.png" className='logo-pic' alt="Todo" />
            )}
            {props.ticket.status==='In progress' && props.groupBy !== 'status' && (
                <img src="https://cdn-icons-png.flaticon.com/128/5460/5460873.png" className='logo-pic' alt="In progress" />
            )}
            {props.ticket.status==='Done' && props.groupBy !== 'status' && (
                <img src="https://cdn-icons-png.flaticon.com/128/5461/5461184.png" className='logo-pic' alt="Done" />
            )}
            {props.ticket.status==='Cancelled' && props.groupBy !== 'status' && (
                <img src="https://cdn-icons-png.flaticon.com/128/1008/1008968.png" className='logo-pic' alt="Cancelled" />
            )}
            <p className='card-desc bold'>
                {props.ticket.title}
            </p>
        </div>
        <div className='card-bottom'>
        {props.groupBy !== 'priority' && (
            <div className='component-1 pointer'>
                <MoreHorizontal className='icon'/>
            </div>
            )}
            <div className='component-2 pointer'>
                <img
                src="https://cdn-icons-png.flaticon.com/128/5720/5720434.png" 
                className='icon'
                alt='logo'
                />
                <p>{props.ticket.tag[0]}</p>
            </div>
        </div>
    </div>
  );
};

export default Card
